local a=1.0;RegisterNetEvent('InteractSound_CL:PlayOnOne')AddEventHandler('InteractSound_CL:PlayOnOne',function(b,c)SendNUIMessage({transactionType='playSound',transactionFile=b,transactionVolume=c})end)RegisterNetEvent('InteractSound_CL:PlayOnAll')AddEventHandler('InteractSound_CL:PlayOnAll',function(b,c)SendNUIMessage({transactionType='playSound',transactionFile=b,transactionVolume=c})end)RegisterNetEvent('InteractSound_CL:PlayWithinDistance')AddEventHandler('InteractSound_CL:PlayWithinDistance',function(d,e,b,c)local f=GetEntityCoords(GetPlayerPed(-1))local g=GetEntityCoords(GetPlayerPed(GetPlayerFromServerId(d)))local h=Vdist(f.x,f.y,f.z,g.x,g.y,g.z)local i=1.0-c/e*h;print(i)if 0<=i then SendNUIMessage({transactionType='playSound',transactionFile=b,transactionVolume=i})end end)RegisterNetEvent('InteractSound_CL:PlayWithinDistanceOS')AddEventHandler('InteractSound_CL:PlayWithinDistanceOS',function(j,e,b,c)local f=GetEntityCoords(GetPlayerPed(-1))local h=Vdist(f.x,f.y,f.z,j.x,j.y,j.z)if h<=e then SendNUIMessage({transactionType='playSound',transactionFile=b,transactionVolume=c})end end)
